document.addEventListener('DOMContentLoaded',()=>{document.body.classList.add('js-active');const observerOptions={root:null,rootMargin:'0px',threshold:0.1};const observer=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('visible');observer.unobserve(entry.target);}});},observerOptions);const scrollElements=document.querySelectorAll('.scroll-reveal');scrollElements.forEach(el=>observer.observe(el));});